mapper.select:
SELECT * FROM ( SELECT ROWNUM as ROWNUMBER, ofps.COM_CD_NM AS OFPS_CD_NM, reso.COM_CD_NM AS RESO_CD_NM, dept.DEPT_NM AS BLTO_DEPT_NM, emp.*, IBIZ_COM_CD_NM('HDOC_STAT_CD', HDOC_STAT_CD) AS HDOC_STAT_CD_NM, COUNT(*) OVER() AS TOTAL_CNT , ( SELECT CUST_ID FROM CMST010T WHERE CUST_ROLE_CLSF_CD = 'C' AND EMP_ID = emp.EMP_ID AND ROLE_SEQ = ( SELECT MIN(ROLE_SEQ) FROM CMST010T WHERE CUST_ROLE_CLSF_CD = 'C' AND EMP_ID = emp.EMP_ID ) ) AS CUST_ID, ( SELECT BLTO_COMP_NM FROM CMST000T WHERE CUST_ID = ( SELECT CUST_ID FROM CMST010T WHERE CUST_ROLE_CLSF_CD = 'C' AND EMP_ID = emp.EMP_ID AND ROLE_SEQ = ( SELECT MIN(ROLE_SEQ) FROM CMST010T WHERE CUST_ROLE_CLSF_CD = 'C' AND EMP_ID = emp.EMP_ID ) ) ) AS COMP_NM FROM HMST300T emp JOIN HMST100T dept ON emp.BLTO_DEPT_ID = dept.DEPT_ID JOIN ( SELECT cd.* FROM ACOM020T mappinggp JOIN ACOM010T cd ON mappinggp.COM_GRP_CD = cd.COM_GRP_CD WHERE mappinggp.CLMN_NM = 'OFPS_CD' ) ofps ON ofps.COM_CD = emp.OFPS_CD JOIN ( SELECT cd.* FROM ACOM020T mappinggp JOIN ACOM010T cd ON mappinggp.COM_GRP_CD = cd.COM_GRP_CD WHERE mappinggp.CLMN_NM = 'RESO_CD' ) reso ON reso.COM_CD = emp.RESO_CD WHERE 1 = 1
AND HDOC_STAT_CD = #{hdocStatCd}
AND UPPER(EMP_NM) LIKE '%'||UPPER(#{empNm})||'%'
AND (BLTO_DEPT_ID IN ( SELECT DEPT_ID FROM HMST100T START WITH HGRK_DEPT_ID = #{bltoDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ) OR BLTO_DEPT_ID = #{bltoDeptId} )
) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}
